import { createSelector } from "reselect";

const COLLECTION_ID_MAP = {
    hats:1,
    sneakers:2,
    jackets:3,
    womens:4,
    mens:5
}


const ShopCollection = state => state.Shop;

export const SelectShopCollection = createSelector(
    [ShopCollection],

    Shop => Shop.ShopData
)
