import { takeLatest, put, all, call} from "@redux-saga/core/effects";
import UserActionTypes from "./UserTypes";
import {signInWithPopup} from "firebase/auth";
import {signInWithEmailAndPassword } from "firebase/auth"

import { GoogleProvider, auth, createUserProfileDocument } from "../../FireBase/Firebase-util";
import { SignInFailure, SignInSuccess } from "./UserAction";

export function* getSnapShotForUser(userAuth){
    try{
        const userRef = yield call(createUserProfileDocument, user);
        const userSnapshot = yield userRef.get();
        yield put(
                     SignInSuccess({id:userSnapshot.id, ...userSnapshot.data()})
                 )


    }
    catch(error){
        yield put(
            SignInFailure(error)
        )

    }





}

export function* SignInWithGoogle(){
    try{

        const {user} = yield signInWithPopup(auth, GoogleProvider);
       yield getSnapShotForUser(user)

    }
    catch(error){
        yield put(
            SignInFailure(error)
        )

    }


}

export function* SignInWithEmail({payload: {email , password}}){
    try{
        const {user} = yield signInWithEmailAndPassword( auth,
            email,
            password)
     yield getSnapShotForUser(user)

    }catch(error){
        yield put(SignInFailure(error))
    }
}

export function* onGoogleSignInStart(){

    yield takeLatest(UserActionTypes.GOOGLE_SIGN_IN_START, SignInWithGoogle)

}

export function* onEmailSignInstart(){
    yield takeLatest(UserActionTypes.EMAIL_SIGN_IN_START, SignInWithEmail )
}

export function* UserSagas(){
    yield all([call(onGoogleSignInStart), call(onEmailSignInstart)])
}
