import CartActionTypes from "./CartTypes"

const INITIAL_STATE ={
    hidden : true,
    CartItems: []
}

const CartReducer =(state = INITIAL_STATE, action)=>{

switch(action.type){

    case CartActionTypes.TOGGLE_CART_HIDDEN:
        return{

            ...state,
            hidden : !state.hidden
        }
    case CartActionTypes.TOGGLE_CART_HIDDEN:
        return{

                ...state,
                CartItems:[...state.CartItems,action.payload]
            }
        default:
            return state
}
}

export default CartReducer
