import { even } from "prelude-ls";
import react, { Component } from "react";
import FormInput from "../Form-Input/Form-Input";
import "./SignIn.Styles.scss";



class SignIn extends Component {

    constructor(props){

        super(props)

        this.state={

            email:"",
            password:""
        }

    }

    handelSubmit = (event) =>{

        event.preventDefault();

        this.setState({email:'', password:''})
    }
    handelChange = (event) => {
        const {value, name} = event.target;

        this.setState({[name]:value});
    }

    render(){
        return(

            <div className="sign-in">
                <h2>I already have and account</h2>
                <span>Sign in with your email and password</span>
                <form onSubmit={this.handelSubmit}>
                    <FormInput name="email" label="email" type="email" handelChange={this.handelChange} value={this.state.email} required/>
                    <label>Email</label> 
                    <FormInput name="password" type="password" handelChange={this.handelChange} value={this.state.password} required/>
                    <label>Password</label> 

                    <input type="submit" value="Submit Form"/>

                </form>
            </div>

        )


    }
}

export default SignIn
