import {FirstName, LastName} from "./../../../App"

const ComC = (props) => {
    console.log("this ins c",props)


    return (

        <>
        <FirstName.Consumer>
        {(collection)=>{
            const {title , items} =collection

            return (<>
            <h1>{title}</h1>

            {
                   items.map(item=> <h2 key={item.id} >{item.name}</h2>)
               }
               </>

            )
        }
    }

      </FirstName.Consumer>

        </>

    )


}

export default ComC


