import React from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { createStructuredSelector } from "reselect";
import { AddItem } from "../../redux/Cart/CartAction";
import { selectCurrentUser } from "../../redux/User/UserSelector";
import CustomButton from "../custom-button/CustomButton";
import "./CollectionItems.Styles.scss"

const CollectionItems = ({item,currentUser, AddItem}) =>{
    const {name, price, imageUrl} = item
    return(
    <div className="collection-item">
    <div style={{backgroundImage:`url(${imageUrl})`}} className="image" />


    <div className="collection-footer">
        <span className="name">{name}</span>
        <span className="price">{price}</span>
    </div>
    { currentUser ?
        <CustomButton onClick={()=> AddItem(item)} inverted>Add To Cart</CustomButton>
        :
       <Link to="/Signin"> <CustomButton inverted>Login For Add to Cart</CustomButton></Link>
    }

</div>
    )
}


const mapDispatchToProps = dispatch => ({

    AddItem: item => dispatch(AddItem(item))
})

const mapStateToProps =  createStructuredSelector({
    currentUser: selectCurrentUser,

  })
export default connect(mapStateToProps, mapDispatchToProps)(CollectionItems)
