import SHOP_DATA from "./ShopData";
import ShopActionTypes from "./ShopTypes";

const INITIAL_STATE = {
  ShopData: SHOP_DATA
};

const ShopReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {

    case ShopActionTypes.UPDATE_COLLECTION:
     return {
  ...state,
  ShopData: action.payload
    }

    default:
      return state;
  }
};

export default ShopReducer;
