import { takeLatest, put, all, call} from "@redux-saga/core/effects";
import UserActionTypes from "./UserTypes";
import {signInWithPopup, signInWithEmailAndPassword} from "firebase/auth";

import { GoogleProvider, auth, createUserProfileDocument } from "../../FireBase/Firebase-util";
import { EmailSignInFailure, GoogleSignInFailure, GoogleSignInSuccess } from "./UserAction";

export function* SignInWithGoogle(){
    try{

        const {user} = yield signInWithPopup(auth, GoogleProvider);
        const userRef = yield call(createUserProfileDocument, user);
        const userSnapshot = yield userRef.get();
        yield put(
            GoogleSignInSuccess({id:userSnapshot.id, ...userSnapshot.data()})
        )


    }
    catch(error){
        yield put(
            GoogleSignInFailure(error)
        )

    }


}

export function* SignInWithEmailAndPassword({payload: email , password}){
    try{
        const {user} = yield signInWithEmailAndPassword(auth, email, password);
        const userRef = yield call(createUserProfileDocument, user);
        const userSnapshot = yield userRef.get();
        yield put(
            GoogleSignInSuccess({id:userSnapshot.id, ...userSnapshot.data()})
        )


    }catch(error){
        yield put(EmailSignInFailure(error))
    }
}

export function* onGoogleSignInStart(){

    yield takeLatest(UserActionTypes.GOOGLE_SIGN_IN_START, SignInWithGoogle)

}

export function* onEmailSignInstart(){
    yield takeLatest(UserActionTypes.EMAIL_SIGN_IN_START, SignInWithEmailAndPassword )
}

export function* UserSagas(){
    yield all([call(onGoogleSignInStart)])
}
