import { connect } from "react-redux"
import { createStructuredSelector } from "reselect"
import { selectCurrentUser } from "../User/UserSelector"
import CartActionTypes from "./CartTypes"
import { AddItemToCart, RemoveItemsFromCart } from "./CartUtils"


const INITIAL_STATE ={
    hidden : true,
    CartItems: []
}

const CartReducer =(state = INITIAL_STATE, action,{currentUser})=>{

switch(action.type){

    case CartActionTypes.TOGGLE_CART_HIDDEN:
        return{

            ...state,
            hidden : !state.hidden
        }
    case CartActionTypes.ADD_ITEM:
        return{

                ...state,
                CartItems: AddItemToCart(state.CartItems, action.payload,currentUser)
            }
    case CartActionTypes.CLEAR_ITEM_FROM_CART:
        return{

                ...state,
                CartItems: state.CartItems.filter(cartItem => cartItem.id !== action.payload.id)
            }

    case CartActionTypes.REMOVE_ITEM:
         return{
                     ...state,
                 CartItems: RemoveItemsFromCart(state.CartItems, action.payload)
                    }
        default:
            return state
}
}
const mapStateToProps =  createStructuredSelector({
    currentUser: selectCurrentUser,

  })


export default connect(mapStateToProps)(CartReducer)
